DROP TABLE IF EXISTS Products; 
CREATE TABLE Products (
    id INT primary key,  /* id la khóa chính*/
    name NVARCHAR(100) NOT NULL,
    price FLOAT NOT NULL check (price>=0), /* giá phải >=0**/
    stock INT NOT NULL check (stock>=0), /* tồn kho >=0*/
	type NVARCHAR(100) NOT NULL,
    season NVARCHAR(100) NOT NULL,
	UNIQUE(name, type) /* không trùng tên và loại sản phẩm*/
);
